// JavaScript Document
$(document).ready(function(){divloading('#profile-wrapper');$.getJSON('calls/getprofile.php',function(r){if(r=='no'){$(this).append('Failed to load your information. Please contact Administrator!');}else{$.each(r,function(i,d){$("#editprofile").find("input[name='firstname']").val(d.firstname);$("#editprofile").find("input[name='lastname']").val(d.lastname);$("#editprofile").find("input[name='email']").val(d.email);$("#editprofile").find("input[name='university']").val(d.university);$("#editprofile").find("input[name='enroll']").val(d.year);$("#editprofile").find("input[name='role']").val(d.role);$("#editprofile").find("input[name='active']").val(d.active);$("#editprofile").find("input[name='native']").filter('[value="'+d.native+'"]').attr('checked', true);$("select#age option[value='"+d.age+"']").attr("selected", "selected");});$("#f1w").show();}});$('#profile-wrapper').unblock();$("#resetpass").click(function(){$("#f2w").slideDown();});$('#editprofile').submit(function(){$(this).ajaxSubmit({beforeSubmit:divloading('#result1'),success:function(r){if(r=='success'){$('#result1').empty().append('<span class="success">Changes saved.</span>').unblock();}else{$('#result1').empty().append('<span class="error">'+r+'</span>').unblock();}}});return false;});$('#resetpassf').submit(function(){$(this).ajaxSubmit({beforeSubmit:divloading('#result2'),success:function(r){if(r=='success'){$('#result2').empty().append('<span class="success">New password saved.</span>').unblock();}else{$('#result2').empty().append('<span class="error">'+r+'</span>').unblock();}}});return false;});});function divloading(div){$(div).block({message:'<img src="../../../Assets/images/loader-bigcircle.gif" alt="loading...">',css:{background:'none',border:'none',padding:100},overlayCSS:{background:'#fff'}});}